/*
** ===================================================================
**     Method      :  DA1_BufferInterrupt (component DAC_LDD)
**
**     Description :
**         DAC Buffer interrupt handler.
**         This method is internal. It is used by Processor Expert only.
** ===================================================================
*/
void DA1_BufferInterrupt(LDD_RTOS_TISRParameter _isrParameter)
{
  /* {MQX RTOS Adapter} ISR parameter is passed as parameter from RTOS interrupt dispatcher */
  DA1_TDeviceData* DeviceDataPtr = (DA1_TDeviceData*)_isrParameter;
  uint8_t State;

  State = DAC_PDD_GetInterruptFlags(DAC0_BASE_PTR); /* Store pending interrupts state */
  State &= DAC_PDD_GetInterruptMask(DAC0_BASE_PTR); /* Handle only enabled interrupts */
  DAC_PDD_ClearInterruptFlags(DAC0_BASE_PTR,State); /* Clear flags */
  if ((State & DAC_PDD_BUFFER_START_INTERRUPT) != 0x00U) {
    DA1_OnBufferStart(DeviceDataPtr->UserDataPtr); /* Call user event */
  }
  if ((State & DAC_PDD_BUFFER_WATERMARK_INTERRUPT) != 0x00U) {
    DA1_OnBufferWatermark(DeviceDataPtr->UserDataPtr); /* Call user event */
  }
  if ((State & DAC_PDD_BUFFER_END_INTERRUPT) != 0x00U) {
    DA1_OnBufferEnd(DeviceDataPtr->UserDataPtr); /* Call user event */
  }
}

/*
** ===================================================================
**     Method      :  DA1_SetClockConfiguration (component DAC_LDD)
**
**     Description :
**         This method changes the clock configuration. During a clock 
**         configuration change the component changes it's setting 
**         immediately upon a request.
**         This method is internal. It is used by Processor Expert only.
** ===================================================================
*/
void DA1_SetClockConfiguration(LDD_TDeviceData *DeviceDataPtr, LDD_TClockConfiguration ClockConfiguration)
{
  switch (ClockConfiguration) {
    case CPU_CLOCK_CONFIG_0:
      ((DA1_TDeviceData*)DeviceDataPtr)->EnMode = TRUE; /* Set the flag "device enabled" in the actual speed CPU mode */
      if (((DA1_TDeviceData*)DeviceDataPtr)->EnUser) { /* Is device enabled by user? */
        DAC_PDD_EnableDevice(DAC0_BASE_PTR,PDD_ENABLE); /* If yes, enable device */
      }
      break;
    default:
      ((DA1_TDeviceData*)DeviceDataPtr)->EnMode = FALSE; /* Set the flag "device disabled" in the actual speed CPU mode */
      DAC_PDD_EnableDevice(DAC0_BASE_PTR,PDD_DISABLE); /* Disable device */
      break;
  }
}



